PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE d1_migrations(
		id         INTEGER PRIMARY KEY AUTOINCREMENT,
		name       TEXT UNIQUE,
		applied_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
);
CREATE TABLE departments (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT UNIQUE NOT NULL,
  approver_id INTEGER,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (approver_id) REFERENCES employees(id)
);
CREATE TABLE approval_routes (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  application_type TEXT NOT NULL CHECK(application_type IN ('expense', 'paid_leave', 'overtime')),
  department_id INTEGER,
  min_amount REAL,
  max_amount REAL,
  approver_level INTEGER NOT NULL DEFAULT 1,
  approver_id INTEGER NOT NULL,
  auto_approve BOOLEAN DEFAULT 0,
  auto_approve_conditions TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (department_id) REFERENCES departments(id),
  FOREIGN KEY (approver_id) REFERENCES employees(id)
);
CREATE TABLE sessions (
  id TEXT PRIMARY KEY,
  employee_id INTEGER NOT NULL,
  expires_at DATETIME NOT NULL,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (employee_id) REFERENCES employees(id)
);
CREATE TABLE attendance_records (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  employee_id INTEGER NOT NULL,
  date DATE NOT NULL,
  status TEXT NOT NULL CHECK(status IN ('present', 'absent', 'paid_leave', 'sick_leave', 'special_leave', 'holiday', 'company_holiday')),
  note TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (employee_id) REFERENCES employees(id) ON DELETE CASCADE,
  UNIQUE(employee_id, date)
);
CREATE TABLE company_holidays (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  date DATE NOT NULL UNIQUE,
  name TEXT NOT NULL,
  type TEXT NOT NULL CHECK(type IN ('national_holiday', 'year_end', 'obon', 'golden_week', 'other')),
  description TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE attendance_summary (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  employee_id INTEGER NOT NULL,
  year INTEGER NOT NULL,
  month INTEGER NOT NULL,
  total_working_days INTEGER NOT NULL,
  actual_attendance_days INTEGER NOT NULL,
  absent_days INTEGER NOT NULL,
  paid_leave_days INTEGER NOT NULL,
  attendance_rate REAL NOT NULL,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (employee_id) REFERENCES employees(id) ON DELETE CASCADE,
  UNIQUE(employee_id, year, month)
);
CREATE TABLE paid_leave_grants (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  employee_id INTEGER NOT NULL,
  grant_date DATE NOT NULL,  
  granted_days REAL NOT NULL,  
  remaining_days REAL NOT NULL,  
  expiration_date DATE NOT NULL,  
  grant_reason TEXT DEFAULT 'annual',  
  grant_type TEXT DEFAULT 'auto' CHECK(grant_type IN ('auto', 'manual')),  
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (employee_id) REFERENCES employees(id) ON DELETE CASCADE
);
CREATE TABLE paid_leave_import_logs (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  imported_by INTEGER NOT NULL,  
  import_date DATETIME DEFAULT CURRENT_TIMESTAMP,
  file_name TEXT,
  total_records INTEGER DEFAULT 0,
  success_records INTEGER DEFAULT 0,
  error_records INTEGER DEFAULT 0,
  error_details TEXT,  
  FOREIGN KEY (imported_by) REFERENCES employees(id) ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "paid_leave_usages" (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  employee_id INTEGER NOT NULL,
  application_id INTEGER,  
  grant_id INTEGER,
  usage_date DATE NOT NULL,
  used_days REAL NOT NULL,
  leave_type TEXT NOT NULL CHECK(leave_type IN ('full_day', 'half_day', 'hourly')),
  leave_hours REAL,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (employee_id) REFERENCES employees(id) ON DELETE CASCADE,
  FOREIGN KEY (application_id) REFERENCES applications(id) ON DELETE CASCADE,
  FOREIGN KEY (grant_id) REFERENCES paid_leave_grants(id) ON DELETE SET NULL
);
CREATE TABLE employee_invitations (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  email TEXT UNIQUE NOT NULL,
  role TEXT NOT NULL CHECK(role IN ('employee', 'approver', 'admin', 'system_admin')),
  token TEXT UNIQUE NOT NULL,
  invited_by INTEGER NOT NULL,
  expires_at DATETIME NOT NULL,
  used_at DATETIME,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (invited_by) REFERENCES employees(id) ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "employees" (
  id INTEGER PRIMARY KEY,
  email TEXT UNIQUE NOT NULL,
  password_hash TEXT NOT NULL,
  name TEXT NOT NULL,
  department TEXT,
  position TEXT,
  manager_id INTEGER,
  employment_type TEXT DEFAULT 'full_time' CHECK(employment_type IN ('full_time', 'part_time', 'contract')),
  hire_date DATE,
  termination_date DATE,
  role TEXT DEFAULT 'employee' CHECK(role IN ('employee', 'approver', 'admin', 'system_admin', 'viewer')),
  paid_leave_balance REAL DEFAULT 10.0,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  current_attendance_rate REAL DEFAULT 1.0,
  carryover_days REAL DEFAULT 0.0,
  next_grant_date DATE,
  service_years INTEGER DEFAULT 0,
  approver_id INTEGER
, second_approver_id INTEGER);
CREATE TABLE IF NOT EXISTS "feature_requests" (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  employee_id INTEGER NOT NULL,
  title TEXT NOT NULL,
  description TEXT NOT NULL,
  category TEXT CHECK(category IN ('new_feature', 'improvement', 'bug', 'other')),
  priority TEXT DEFAULT 'medium' CHECK(priority IN ('low', 'medium', 'high')),
  status TEXT DEFAULT 'pending' CHECK(status IN ('pending', 'in_progress', 'completed', 'on_hold', 'rejected')),
  admin_comment TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (employee_id) REFERENCES employees(id)
);
CREATE TABLE IF NOT EXISTS "applications" (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  application_type TEXT NOT NULL CHECK(application_type IN ('expense', 'paid_leave', 'overtime', 'wish')),
  applicant_id INTEGER NOT NULL,
  status TEXT NOT NULL CHECK(status IN ('draft', 'pending', 'approved', 'rejected', 'sent_back', 'withdrawn')),
  title TEXT NOT NULL,
  description TEXT,
  
  -- Expense fields
  expense_amount REAL,
  expense_category TEXT,
  expense_date DATE,
  payment_method TEXT CHECK(payment_method IN ('cash', 'credit_card', 'company_card', 'transfer')),
  payment_status TEXT CHECK(payment_status IN ('unpaid', 'scheduled', 'paid')),
  account_code TEXT,
  payee TEXT,
  invoice_number TEXT,
  expense_notes TEXT,
  
  -- Leave fields
  leave_start_date DATE,
  leave_end_date DATE,
  leave_type TEXT CHECK(leave_type IN ('full_day', 'half_day')),
  leave_hours REAL,
  
  -- Overtime fields
  overtime_date DATE,
  planned_overtime_hours REAL,
  planned_overtime_hours_before REAL,
  planned_overtime_hours_after REAL,
  actual_overtime_hours REAL,
  overtime_reason TEXT,
  
  -- Wish fields
  wish_item_name TEXT,
  wish_reason TEXT,
  wish_estimated_cost REAL,
  wish_priority TEXT CHECK(wish_priority IN ('low', 'medium', 'high')),
  wish_url TEXT,
  
  -- Common fields
  attachment_url TEXT,
  current_approver_id INTEGER,
  final_approved_at DATETIME,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP, application_number TEXT, second_current_approver_id INTEGER,
  
  FOREIGN KEY (applicant_id) REFERENCES employees(id),
  FOREIGN KEY (current_approver_id) REFERENCES employees(id)
);
CREATE TABLE IF NOT EXISTS "approval_history" (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  application_id INTEGER NOT NULL,
  approver_id INTEGER NOT NULL,
  action TEXT NOT NULL CHECK(action IN ('submitted', 'approved', 'rejected', 'sent_back', 'auto_approved', 'withdrawn')),
  comment TEXT,
  approval_level INTEGER DEFAULT 1,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  FOREIGN KEY (application_id) REFERENCES applications(id),
  FOREIGN KEY (approver_id) REFERENCES employees(id)
);
DELETE FROM sqlite_sequence;
