CREATE INDEX idx_company_holidays_type ON company_holidays(type);
CREATE INDEX idx_attendance_summary_employee ON attendance_summary(employee_id);
CREATE INDEX idx_attendance_summary_year_month ON attendance_summary(year, month);
CREATE INDEX idx_paid_leave_grants_employee ON paid_leave_grants(employee_id);
CREATE INDEX idx_paid_leave_grants_grant_date ON paid_leave_grants(grant_date);
CREATE INDEX idx_paid_leave_grants_expiration ON paid_leave_grants(expiration_date);
CREATE INDEX idx_import_logs_date ON paid_leave_import_logs(import_date);
CREATE INDEX idx_import_logs_imported_by ON paid_leave_import_logs(imported_by);
CREATE INDEX idx_paid_leave_usages_employee ON paid_leave_usages(employee_id);
CREATE INDEX idx_paid_leave_usages_date ON paid_leave_usages(usage_date);
CREATE INDEX idx_invitations_token ON employee_invitations(token);
CREATE INDEX idx_invitations_email ON employee_invitations(email);
CREATE INDEX idx_invitations_expires ON employee_invitations(expires_at);
CREATE INDEX idx_employees_email ON employees(email);
CREATE INDEX idx_employees_department ON employees(department);
CREATE INDEX idx_employees_manager ON employees(manager_id);
CREATE INDEX idx_employees_approver ON employees(approver_id);
CREATE INDEX idx_employees_hire_date ON employees(hire_date);
CREATE INDEX idx_employees_role ON employees(role);
CREATE INDEX idx_feature_requests_employee ON feature_requests(employee_id);
CREATE INDEX idx_feature_requests_status ON feature_requests(status);
CREATE INDEX idx_feature_requests_created ON feature_requests(created_at);
CREATE INDEX idx_applications_applicant ON applications(applicant_id);
CREATE INDEX idx_applications_status ON applications(status);
CREATE INDEX idx_applications_type ON applications(application_type);
CREATE INDEX idx_applications_created ON applications(created_at);
CREATE INDEX idx_applications_approver ON applications(current_approver_id);
CREATE INDEX idx_employees_second_approver ON employees(second_approver_id);
